import React, { Component } from 'react';
import Axios from 'axios';
import * as firebase1 from 'firebase';
import firebase from './firebase';




class Order extends Component {
    constructor(props) {
        super(props)
        firebaseInit()
        this.state = {
            orders: []
        }
    }


    componentWillMount = () => {
        dataupdate = () => {
            firebase.database().ref("users/").once("value")
                .then((snapshot) => {
                    this.setState({
                        orders: snapshot.val()

                    });
                })
        }   


    }




    componentDidMount = () => {
        // this.dataupdate();
        Axios.get('https://simple-30744.firebaseio.com/users.json')
            .then(res => {
                console.log(res.data);
                const fetchedorders = [];
                for (let key in res.data) {
                    fetchedorders.push({
                        ...res.data[key],
                        id: key
                    })
                }
                this.setState({ orders: fetchedorders })
                console.log(this.state.orders)
            })

    }



    render() {

        let orders = this.state.orders.map(order => (
            <li key={order.id} style={{ color: 'green' }}>
                <div>{order.name}</div>
                <div>{order.id}</div>
                <div><a href={order.image}>Image</a></div>
                <button>Send</button>
            </li>
        ))
        return (
            <div>
                <ul>{orders}</ul>
            </div>
        )
    }
}

export default Order;
