import React, { Component } from 'react';
import Axios from 'axios';
// import * as firebase from 'firebase';
import firebase1 from './firebase.js';




class Order extends Component {
    constructor(props) {
        super(props)
        // firebaseInit()
        this.state = {
            orders: []
        }
        this.itemsRef = firebase1.database().ref('users')
        this.itemsRef.on('value', data => {
            const fetchedorders = [];
            console.log(data.val());
            for (let key in data.val()) {
                fetchedorders.push({
                    ...data.val()[key],
                    id: key
                })
            }
            this.setState({
                orders: fetchedorders
            })
        })
    }




    componentDidMount = () => {
        // this.dataupdate();
        Axios.get('https://simple-30744.firebaseio.com/users.json')
            .then(res => {
                console.log(res.data);
                const fetchedorders = [];
                for (let key in res.data) {
                    fetchedorders.push({
                        ...res.data[key],
                        id: key
                    })
                }
                this.setState({ orders: fetchedorders })
                console.log(this.state.orders)
            })

    }

    removeorders = (e,order) =>{
        e.preventDefault();
        Axios.delete('https://simple-30744.firebaseio.com/users.json/{order.id}')
        .then(res=>console.log(res));
    }



    render() {

        let orders = this.state.orders.map(order => (
            <li key={order.id} style={{ color: 'green' }}>
                <div>{order.name}</div>
                <div>{order.userid}</div>
                <div><a href={order.image}>Image</a></div>
                <button>Send</button>
                <button type="submit" onClick={e => this.removeorders(e, order)}>Delete</button>
            </li>
        ))
        return (
            <div>
                <ul>{orders}</ul>
            </div>
        )
    }
}

export default Order;
