const initialState = {
    token: null
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'login':
            return {
                ...state,
                token: action.value
            }
        case 'authcheck':
            return {
                ...state,
                token: localStorage.getItem('token')
            }
        default:
            return localStorage.getItem('token');
    }
};

export default reducer;
