import React, { Component } from 'react';
import Axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

class List extends Component {
    state = {
        list: [],
        data: {
            mname: '',
            mrq: '',
            maq: '',
            price: '',
            total: ''
        }
    }


    namechangeHandler = (e) => {
        this.setState({
            data: {
                ...this.state.data,
                mname: e.target.value
            }
        })
    }
    mrqchangeHandler = (e) => {
        this.setState({
            data: {
                ...this.state.data,
                mrq: e.target.value
            }
        })
    }
    maqchangeHandler = (e) => {
        this.setState({
            data: {
                ...this.state.data,
                maq: e.target.value
            }
        })
    }
    pricechangeHandler = (e) => {
        this.setState({
            data: {
                ...this.state.data,
                price: e.target.value
            }
        })
    }
    totalchangeHandler = (e) => {
        this.setState({
            data: {
                ...this.state.data,
                total: e.target.value
            }
        })
    }

    hold = (e) => {
        e.preventDefault();
    }

    listmedicine = () =>{
    this.state.list.push({
        ...this.state.data,
        id:Math.random()
    })
    }

    render() {

        return (
            <div>
                <form class="needs-validation" novalidate onSubmit={this.hold}>
                    <div class="form-row">
                        <div class="col-md-2">
                            <label for="validationCustom01">Medicine Name</label>
                            <input type="text" class="form-control" onChange={(e) => this.namechangeHandler(e)} id="validationCustom01" placeholder="Medicine Name" />
                            <div class="valid-feedback">
                                Looks good!
                         </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Medicine required Quantity</label>
                            <input type="text" class="form-control" onChange={(e) => this.mrqchangeHandler(e)} id="validationCustom02" placeholder="Medicine required Quantity" />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Medicine available Quantity</label>
                            <input type="text" class="form-control" onChange={(e) => this.maqchangeHandler(e)} id="validationCustom02" placeholder="Medicine available Quantity" />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Price Per Unit</label>
                            <input type="text" class="form-control" onChange={(e) => this.pricechangeHandler(e)} id="validationCustom02" placeholder="Price Per Unit" r />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Total</label>
                            <input type="text" class="form-control" onChange={(e) => this.totalchangeHandler(e)} id="validationCustom02" placeholder="Total" />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <button class="btn btn-success" style={{marginBottom:"10px"}} onClick={() => this.props.addmedicine(this.state.data)}>Send</button><br></br>
                            <button class="btn btn-success" onClick={this.listmedicine}>Add</button>
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}


export default List;
