        return (
            <div>
                <form class="needs-validation" novalidate>
                    <div class="form-row">
                        <div class="col-md-2">
                            <label for="validationCustom01">Medicine Name</label>
                            <input type="text" class="form-control" onChange={(e)=>this.namechangeHandler(e)} id="validationCustom01" placeholder="Medicine Name" required />
                            <div class="valid-feedback">
                                Looks good!
                         </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Medicine required Quantity</label>
                            <input type="text" class="form-control" onChange={(e)=>this.mrqchangeHandler(e)} id="validationCustom02" placeholder="Medicine required Quantity" required />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Medicine available Quantity</label>
                            <input type="text" class="form-control" onChange={(e)=>this.maqchangeHandler(e)} id="validationCustom02" placeholder="Medicine available Quantity" required />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Price Per Unit</label>
                            <input type="text" class="form-control" onChange={(e)=>this.pricechangeHandler(e)} id="validationCustom02" placeholder="Price Per Unit" required />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label for="validationCustom02">Total</label>
                            <input type="text" class="form-control" onChange={(e)=>this.totalchangeHandler(e)} id="validationCustom02" placeholder="Total" required />
                            <div class="valid-feedback">
                                Looks good!
                           </div>
                        </div>
                        <div class="col-md-2">
                            <label>Add</label><br></br>
                            <button class="btn btn-success" onClick={this.addmedicine}>Add</button>
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}


export default List;
