import React, { Component } from 'react';
import Axios from 'axios';
// import * as firebase from 'firebase';
import firebase1 from './firebase.js';
import 'bootstrap/dist/css/bootstrap.min.css';




class Order extends Component {
    constructor(props) {
        super(props)
        // firebaseInit()
        this.state = {
            orders: [],
            rowid: null,
            rowexpand:false


        }
        this.itemsRef = firebase1.database().ref('users')
        this.itemsRef.on('value', data => {
            const fetchedorders = [];
            console.log(data.val());
            for (let key in data.val()) {
                fetchedorders.push({
                    ...data.val()[key],
                    id: key
                })
            }
            this.setState({
                orders: fetchedorders
            })
        })
    }




    componentDidMount = () => {
        // this.dataupdate();
        Axios.get('https://simple-30744.firebaseio.com/users.json')
            .then(res => {
                console.log(res.data);
                const fetchedorders = [];
                for (let key in res.data) {
                    fetchedorders.push({
                        ...res.data[key],
                        id: key
                    })
                }
                this.setState({ orders: fetchedorders })
                console.log(this.state.orders)
            })

    }
    rowExpandHandler = (order) => {
        this.setState({ rowid: order.id })
    }

    removeorders = (e, order) => {
        e.preventDefault();
        Axios.delete('https://simple-30744.firebaseio.com/users/' + order.id + '.json')
            .then(res => console.log(res))
            .catch((err) => {
                console.log(err);
            })
    }



    render() {
        let i = 1;

        let orders = this.state.orders.map(order => (
            <tbody key={order.id} onClick={() => this.rowExpandHandler(order)} >
                <tr style={{ height: "50px" }} >
                    <td scope="row">{order.name}</td>
                    <td>{order.userid}</td>
                    <td><a href={order.image} target="_blank">Image</a></td>
                    <td><button class="btn btn-danger" type="submit" onClick={e => this.removeorders(e, order)}>Delete</button></td>
                </tr>
                {(this.state.rowid === order.id ) ? (<tr>
                    <td colSpan="2" style={{ borderTop: "none" }}>
                        <img style={{ width: "500px",height:"500px" }} src={order.image} />
                    </td>
                    <td style={{ borderTop: "none" }}>
                        <div style={{marginBottom:"30px"}}>
                            <button class="btn btn-primary">
                                Fully Available
                        </button>
                        </div >

                        <div style={{marginBottom:"30px"}} >
                            <button class="btn btn-primary">
                                Partially Available
                        </button>
                        </div>
                        <div class="btn btn-primary">
                            <button class="btn btn-primary">
                                Not Available
                        </button>
                        </div>
                    </td>
                </tr>) : null}

            </tbody>




            // <li key={order.id} style={{ color: 'green' }}>
            //     <div>{order.name}</div>
            //     <div>{order.userid}</div>
            //     <div><a href={order.image} target="_blank">Image</a></div>
            //     <button>Send</button>
            //     <button type="submit" onClick={e => this.removeorders(e, order)}>Delete</button>
            // </li>
        ))
        return (
            <div>
                <div style={{ fontSize: "50px", marginBottom: "50px", marginTop: "20px" }}>Order Details</div>
                <table class="table">
                    <thead>
                        <tr>
                            <th scope="col">Name</th>
                            <th scope="col">UserId</th>
                            <th scope="col">Prescription</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    {orders}
                </table>
            </div>
        )
    }
}

export default Order;
