import React, { Component } from 'react';
import Axios from 'axios';
import * as firebase from 'firebase';
import 'firebase/firestore';


class Order extends Component {
    state = {
        orders: []
    }

    componentDidMount = () => {
        this.dbroot = firebase.database().ref().child('io')
        this.dbroot.on('value', snapshot => {
            let peopleArray = []
            snapshot.forEach((snap) => {
                if (snap.val().active === true) {
                    peopleArray.push(snap.val())
                }
            })
            this.setState({ orders: peopleArray })
        });
        return (
            Axios.get('https://simple-30744.firebaseio.com/users.json')
                .then(res => {
                    console.log(res.data);
                    const fetchedorders = [];
                    for (let key in res.data) {
                        fetchedorders.push({
                            ...res.data[key],
                            id: key
                        })
                    }
                    this.setState({ orders: fetchedorders })
                    console.log(this.state.orders)
                })

        )
    }

    render() {

        let orders = this.state.orders.map(order => (
            <li key={order.id} style={{ color: 'green' }}>
                <div>{order.name}</div>
                <div>{order.id}</div>
                <div><a href={order.image}>Image</a></div>
                <button>Send</button>
            </li>
        ))
        return (
            <div>
                <ul>{orders}</ul>
            </div>
        )
    }
}

export default Order;
