import React, { Component } from 'react';
import Axios from 'axios';
import classes from './auth.css'
import { Redirect } from 'react-router-dom';

class Auth extends Component {

    state = {
        email: '',
        password: '',
        isSignup: true
    }

    onSignup = () => {
        this.setState({
            isSignup: !this.state.isSignup
        })
    }


    emailchangehandler = (e) => {
        this.setState({ email: e.target.value })
    }
    passswordchangehandler = (e) => {
        this.setState({ password: e.target.value })
    }

    submitHandler = (e) => {
        e.preventDefault();
        let url;

        if (this.state.isSignup) {
            url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyPassword?key=AIzaSyDYITZ-jtuH88qB1Vn6tmDYb8BDDoryq0M'
        }
        else {
            url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/signupNewUser?key=AIzaSyDYITZ-jtuH88qB1Vn6tmDYb8BDDoryq0M'
        }

        const userdata = {
            email: this.state.email,
            password: this.state.password
        }

        Axios.post(url, userdata)
            .then(res => {
                console.log(res.data);
                this.props.history.push('/')

            })
            .catch(err => console.log(err))
    }


    render() {
        return (
            <div>

                <div className={classes.Auth}>
                    <form onSubmit={this.submitHandler}>
                        <input type="email" value={this.state.email} onChange={this.emailchangehandler} placeholder="email" />
                        <input type="password" value={this.state.password} onChange={this.passswordchangehandler} placeholder="password" />
                        <button type="submit">SUBMIT</button>
                        <p onClick={this.onSignup}> SWITCH TO {this.state.isSignup ? 'SIGNUP' : 'LOGIN'}</p>
                    </form>

                </div>
            </div>
        )
    }
}


export default Auth;
